function chunkArrayInGroups(arr, size) {
  // Break it up.
  let arrInside = [];
  let newArr = [];

  // console.log(arrInside);
  for (;arr.length > 0;) {
    if (arr.length < size) {
      arrInside = [...arr];
      newArr.push(arrInside);
      console.log(newArr);            
      return newArr;
    }
    // console.log(arr.length)
    for (let i = 0; i < size; i++) {
      arrInside.push(arr.shift());
      // console.log(arrInside);
    }
    newArr.push(arrInside);
    // console.log(newArr);
    arrInside = [];
  }
  return newArr;
}

chunkArrayInGroups(["a", "b", "c", "d"], 2);
chunkArrayInGroups([0, 1, 2, 3, 4, 5], 4);
chunkArrayInGroups([0, 1, 2, 3, 4, 5, 6], 3);
