function findLongestWordLength(str) {
  str = str.split(" ");
  // console.log(str);
  let answer = "";
  for (let i = 1 ;i <= str.length - 1 ;i++) {
    if (str[i].length >= answer.length) {
      answer = str[i];
      console.log(answer);
    }
    if (i == str.length - 1) {
      str = answer;
    }
  }
  return str.length;
}

