  console.log(arr[0])


  for (let letter in arr[1]) {
    match = false;
    // console.log("Simula, match is false");
    for (let letter2 in arr[0]) {
      // console.log(arr[1][letter]);
      // console.log(arr[0][letter2]);  
      // console.log(".");    
      if (arr[0][letter2] == arr[1][letter]) {
        // console.log("trumue!!!");
