function titleCase(str) {
  let lowerLetters = "";
  str = str.split(" ");
  str.forEach((word,index,arr) => {
    lowerLetters = word.slice(1, word.length);
    lowerLetters =  lowerLetters.toLowerCase();
    console.log(lowerLetters);
    arr[index] = `${word[0].toUpperCase()}${lowerLetters}`;
  });
  str = str.join(" ");
  return str;
}

