function getIndexToIns(arr, num) {
  // Find my place in this sorted array.
  arr.push(num);
  arr.sort(function(a, b){return a-b})
  for(let i in arr) {
    if (arr[i] >= num) {
      num = i;
      num = parseInt(num);
      return num;
    }
  }
}

console.log(getIndexToIns([40, 60], 50));
