function sumAll(arr) {
  let sum = 0;
  if (arr[0] - arr[1] > 0) {
    for (let i = 0; arr[0] > arr[1];) {
      arr[0] = arr[0] - i;
      sum += arr[0];
      i = 1;
    }
  }
  else if (arr[0] - arr[1] < 0) {
    for (let i = 0; arr[0] < arr[1];) {
      arr[0] = arr[0] + i;
      sum += arr[0];
      i = 1;
    }
  }
  else {
    return arr[0];
  }
  return sum;
}

